module TabsOnRails

  module Version
    MAJOR = 1
    MINOR = 0
    PATCH = 0
    ALPHA = nil

    STRING = [MAJOR, MINOR, PATCH, ALPHA].compact.join('.')
  end

  VERSION = Version::STRING

end
